import React from 'react'
import { Link, useLocation } from 'react-router-dom'
import { useAuth } from '../contexts/AuthContext'
import { 
  Home, 
  Users, 
  Calendar, 
  Pill, 
  Stethoscope, 
  Building,
  Activity,
  Settings,
  LogOut,
  FileText,
  Shield,
  FlaskConical
} from 'lucide-react'

const Sidebar = () => {
  const location = useLocation()
  const { signOut } = useAuth()

  const navigation = [
    { name: 'Dashboard', href: '/', icon: Home },
    { name: 'Patients', href: '/patients', icon: Users },
    { name: 'Appointments', href: '/appointments', icon: Calendar },
    { name: 'Lab Results', href: '/lab-results', icon: FlaskConical },
    { name: 'Doctors', href: '/doctors', icon: Stethoscope },
    { name: 'Medications', href: '/medications', icon: Pill },
    { name: 'Pharmacies', href: '/pharmacies', icon: Building },
    { name: 'Prescriptions', href: '/prescriptions', icon: FileText },
    { name: 'Vitals', href: '/vitals', icon: Activity },
    { name: 'Immunizations', href: '/immunizations', icon: Shield },
    { name: 'Settings', href: '/settings', icon: Settings },
  ]

  const handleSignOut = async () => {
    try {
      await signOut()
    } catch (error) {
      console.error('Error signing out:', error)
    }
  }

  return (
    <div className="flex flex-col h-full bg-gray-800">
      {/* Logo */}
      <div className="flex items-center justify-center h-16 bg-gray-900">
        <div className="flex items-center">
          <div className="w-8 h-8 bg-primary-600 rounded-lg flex items-center justify-center">
            <Stethoscope className="w-5 h-5 text-white" />
          </div>
          <span className="ml-2 text-white font-bold text-lg">DigiCare</span>
        </div>
      </div>

      {/* Navigation */}
      <nav className="flex-1 px-4 py-6 space-y-2">
        {navigation.map((item) => {
          const isActive = location.pathname === item.href
          return (
            <Link
              key={item.name}
              to={item.href}
              className={`flex items-center px-3 py-2 text-sm font-medium rounded-md transition-colors ${
                isActive
                  ? 'bg-primary-600 text-white'
                  : 'text-gray-300 hover:bg-gray-700 hover:text-white'
              }`}
            >
              <item.icon className="mr-3 h-5 w-5" />
              {item.name}
            </Link>
          )
        })}
      </nav>

      {/* Sign Out */}
      <div className="p-4 border-t border-gray-700">
        <button
          onClick={handleSignOut}
          className="flex items-center w-full px-3 py-2 text-sm font-medium text-gray-300 rounded-md hover:bg-gray-700 hover:text-white transition-colors"
        >
          <LogOut className="mr-3 h-5 w-5" />
          Sign Out
        </button>
      </div>
    </div>
  )
}

export default Sidebar
