import { createClient } from '@supabase/supabase-js'

const supabaseUrl = process.env.REACT_APP_SUPABASE_URL
const supabaseAnonKey = process.env.REACT_APP_SUPABASE_ANON_KEY

// Validate environment variables
if (!supabaseUrl || !supabaseAnonKey) {
  console.error('Missing Supabase environment variables. Please check your .env file.')
  console.error('Required variables: REACT_APP_SUPABASE_URL and REACT_APP_SUPABASE_ANON_KEY')
  throw new Error('Supabase configuration is incomplete. Please set REACT_APP_SUPABASE_URL and REACT_APP_SUPABASE_ANON_KEY in your .env file.')
}

export const supabase = createClient(supabaseUrl, supabaseAnonKey, {
  auth: {
    autoRefreshToken: true,
    persistSession: true,
    detectSessionInUrl: false,
  },
  realtime: {
    params: {
      eventsPerSecond: 10,
    },
  },
})

// Database table names
export const TABLES = {
  USERS: 'users',
  PATIENTS: 'patients',
  HOSPITALS: 'hospitals',
  DOCTORS: 'doctors',
  APPOINTMENTS: 'appointments',
  LAB_RESULTS: 'lab_results',
  PHARMACIES: 'pharmacies',
  MEDICATIONS: 'medications',
  PRESCRIPTIONS: 'prescriptions',
  IMMUNIZATIONS: 'immunizations',
  PAYMENTS: 'payments',
  MEDICAL_RECORDS: 'medical_records',
  EMERGENCY_CONTACTS: 'emergency_contacts',
  PATIENT_VITALS: 'patient_vitals',
  DIAGNOSES: 'diagnoses',
}
