import React, { useState, useEffect } from 'react'
import { supabase } from '../../lib/supabase'
import { X, Save, Calendar, Clock, User, Stethoscope, AlertTriangle } from 'lucide-react'
import toast from 'react-hot-toast'

const AppointmentForm = ({ appointment = null, onClose, onSuccess }) => {
  const [formData, setFormData] = useState({
    patient_id: null,
    doctor_id: null,
    hospital_id: null,
    appointment_date: '',
    appointment_time: '',
    reason: '',
    urgency: 'normal',
    status: 'scheduled',
    notes: ''
  })
  const [patients, setPatients] = useState([])
  const [doctors, setDoctors] = useState([])
  const [hospitals, setHospitals] = useState([])
  const [loading, setLoading] = useState(false)
  const [errors, setErrors] = useState({})

  useEffect(() => {
    fetchData()
    if (appointment) {
      setFormData({
        patient_id: appointment.patient_id || null,
        doctor_id: appointment.doctor_id || null,
        hospital_id: appointment.hospital_id || null,
        appointment_date: appointment.appointment_date || '',
        appointment_time: appointment.appointment_time || '',
        reason: appointment.reason || '',
        urgency: appointment.urgency || 'normal',
        status: appointment.status || 'scheduled',
        notes: appointment.notes || ''
      })
    }
  }, [appointment])

  const fetchData = async () => {
    try {
      const [patientsResult, doctorsResult, hospitalsResult] = await Promise.all([
        supabase.from('patients').select('id, first_name, last_name, email').order('first_name'),
        supabase.from('doctors').select('id, first_name, last_name, specialization').order('first_name'),
        supabase.from('hospitals').select('id, name').order('name')
      ])

      if (patientsResult.error) throw patientsResult.error
      if (doctorsResult.error) throw doctorsResult.error
      if (hospitalsResult.error) throw hospitalsResult.error

      setPatients(patientsResult.data || [])
      setDoctors(doctorsResult.data || [])
      setHospitals(hospitalsResult.data || [])
    } catch (error) {
      console.error('Error fetching data:', error)
      toast.error('Failed to load form data')
    }
  }

  const validateForm = () => {
    const newErrors = {}
    
    if (!formData.patient_id) newErrors.patient_id = 'Patient is required'
    if (!formData.doctor_id) newErrors.doctor_id = 'Doctor is required'
    if (!formData.hospital_id) newErrors.hospital_id = 'Hospital is required'
    if (!formData.appointment_date) newErrors.appointment_date = 'Appointment date is required'
    if (!formData.appointment_time) newErrors.appointment_time = 'Appointment time is required'
    if (!formData.reason.trim()) newErrors.reason = 'Reason is required'

    setErrors(newErrors)
    return Object.keys(newErrors).length === 0
  }

  const handleSubmit = async (e) => {
    e.preventDefault()
    
    if (!validateForm()) {
      toast.error('Please fix the errors in the form')
      return
    }

    setLoading(true)
    try {
      let result
      
      if (appointment) {
        // Update existing appointment
        const { data, error } = await supabase
          .from('appointments')
          .update(formData)
          .eq('id', appointment.id)
          .select()
        
        if (error) throw error
        result = data[0]
        toast.success('Appointment updated successfully!')
      } else {
        // Create new appointment
        const { data, error } = await supabase
          .from('appointments')
          .insert([formData])
          .select()
        
        if (error) throw error
        result = data[0]
        toast.success('Appointment created successfully!')
      }

      onSuccess(result)
      onClose()
    } catch (error) {
      console.error('Error saving appointment:', error)
      toast.error(error.message || 'Failed to save appointment')
    } finally {
      setLoading(false)
    }
  }

  const handleInputChange = (field, value) => {
    setFormData(prev => ({ ...prev, [field]: value }))
    if (errors[field]) {
      setErrors(prev => ({ ...prev, [field]: '' }))
    }
  }

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
      <div className="bg-white rounded-lg shadow-xl w-full max-w-2xl max-h-[90vh] overflow-y-auto">
        <div className="flex items-center justify-between p-6 border-b">
          <h2 className="text-xl font-semibold text-gray-900">
            {appointment ? 'Edit Appointment' : 'Schedule New Appointment'}
          </h2>
          <button
            onClick={onClose}
            className="text-gray-400 hover:text-gray-600"
          >
            <X className="w-6 h-6" />
          </button>
        </div>

        <form onSubmit={handleSubmit} className="p-6 space-y-6">
          {/* Patient and Doctor Selection */}
          <div>
            <h3 className="text-lg font-medium text-gray-900 mb-4 flex items-center">
              <User className="w-5 h-5 mr-2" />
              Patient & Doctor
            </h3>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Patient *
                </label>
                <select
                  value={formData.patient_id || ''}
                  onChange={(e) => handleInputChange('patient_id', e.target.value ? parseInt(e.target.value) : null)}
                  className={`w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-2 focus:ring-primary-500 ${
                    errors.patient_id ? 'border-red-500' : 'border-gray-300'
                  }`}
                >
                  <option value="">Select patient</option>
                  {patients.map(patient => (
                    <option key={patient.id} value={patient.id}>
                      {patient.first_name} {patient.last_name} ({patient.email})
                    </option>
                  ))}
                </select>
                {errors.patient_id && (
                  <p className="text-red-500 text-sm mt-1">{errors.patient_id}</p>
                )}
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Doctor *
                </label>
                <select
                  value={formData.doctor_id || ''}
                  onChange={(e) => handleInputChange('doctor_id', e.target.value ? parseInt(e.target.value) : null)}
                  className={`w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-2 focus:ring-primary-500 ${
                    errors.doctor_id ? 'border-red-500' : 'border-gray-300'
                  }`}
                >
                  <option value="">Select doctor</option>
                  {doctors.map(doctor => (
                    <option key={doctor.id} value={doctor.id}>
                      Dr. {doctor.first_name} {doctor.last_name} ({doctor.specialization})
                    </option>
                  ))}
                </select>
                {errors.doctor_id && (
                  <p className="text-red-500 text-sm mt-1">{errors.doctor_id}</p>
                )}
              </div>
            </div>
          </div>

          {/* Hospital and Schedule */}
          <div>
            <h3 className="text-lg font-medium text-gray-900 mb-4 flex items-center">
              <Calendar className="w-5 h-5 mr-2" />
              Hospital & Schedule
            </h3>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Hospital *
                </label>
                <select
                  value={formData.hospital_id || ''}
                  onChange={(e) => handleInputChange('hospital_id', e.target.value ? parseInt(e.target.value) : null)}
                  className={`w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-2 focus:ring-primary-500 ${
                    errors.hospital_id ? 'border-red-500' : 'border-gray-300'
                  }`}
                >
                  <option value="">Select hospital</option>
                  {hospitals.map(hospital => (
                    <option key={hospital.id} value={hospital.id}>
                      {hospital.name}
                    </option>
                  ))}
                </select>
                {errors.hospital_id && (
                  <p className="text-red-500 text-sm mt-1">{errors.hospital_id}</p>
                )}
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Appointment Date *
                </label>
                <input
                  type="date"
                  value={formData.appointment_date}
                  onChange={(e) => handleInputChange('appointment_date', e.target.value)}
                  min={new Date().toISOString().split('T')[0]}
                  className={`w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-2 focus:ring-primary-500 ${
                    errors.appointment_date ? 'border-red-500' : 'border-gray-300'
                  }`}
                />
                {errors.appointment_date && (
                  <p className="text-red-500 text-sm mt-1">{errors.appointment_date}</p>
                )}
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Appointment Time *
                </label>
                <input
                  type="time"
                  value={formData.appointment_time}
                  onChange={(e) => handleInputChange('appointment_time', e.target.value)}
                  className={`w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-2 focus:ring-primary-500 ${
                    errors.appointment_time ? 'border-red-500' : 'border-gray-300'
                  }`}
                />
                {errors.appointment_time && (
                  <p className="text-red-500 text-sm mt-1">{errors.appointment_time}</p>
                )}
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Urgency Level
                </label>
                <select
                  value={formData.urgency}
                  onChange={(e) => handleInputChange('urgency', e.target.value)}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-primary-500"
                >
                  <option value="low">Low</option>
                  <option value="normal">Normal</option>
                  <option value="high">High</option>
                  <option value="urgent">Urgent</option>
                </select>
              </div>
            </div>
          </div>

          {/* Appointment Details */}
          <div>
            <h3 className="text-lg font-medium text-gray-900 mb-4 flex items-center">
              <Stethoscope className="w-5 h-5 mr-2" />
              Appointment Details
            </h3>
            <div className="space-y-4">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Reason for Visit *
                </label>
                <textarea
                  value={formData.reason}
                  onChange={(e) => handleInputChange('reason', e.target.value)}
                  rows={3}
                  className={`w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-2 focus:ring-primary-500 ${
                    errors.reason ? 'border-red-500' : 'border-gray-300'
                  }`}
                  placeholder="Describe the reason for the appointment"
                />
                {errors.reason && (
                  <p className="text-red-500 text-sm mt-1">{errors.reason}</p>
                )}
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Additional Notes
                </label>
                <textarea
                  value={formData.notes}
                  onChange={(e) => handleInputChange('notes', e.target.value)}
                  rows={3}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-primary-500"
                  placeholder="Any additional notes or instructions"
                />
              </div>

              {appointment && (
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Status
                  </label>
                  <select
                    value={formData.status}
                    onChange={(e) => handleInputChange('status', e.target.value)}
                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-primary-500"
                  >
                    <option value="scheduled">Scheduled</option>
                    <option value="confirmed">Confirmed</option>
                    <option value="in-progress">In Progress</option>
                    <option value="completed">Completed</option>
                    <option value="cancelled">Cancelled</option>
                    <option value="no-show">No Show</option>
                  </select>
                </div>
              )}
            </div>
          </div>

          {/* Form Actions */}
          <div className="flex justify-end space-x-3 pt-6 border-t">
            <button
              type="button"
              onClick={onClose}
              className="px-4 py-2 text-gray-700 bg-gray-100 rounded-md hover:bg-gray-200 focus:outline-none focus:ring-2 focus:ring-gray-500"
            >
              Cancel
            </button>
            <button
              type="submit"
              disabled={loading}
              className="px-4 py-2 bg-primary-600 text-white rounded-md hover:bg-primary-700 focus:outline-none focus:ring-2 focus:ring-primary-500 disabled:opacity-50 disabled:cursor-not-allowed flex items-center"
            >
              {loading ? (
                <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2"></div>
              ) : (
                <Save className="w-4 h-4 mr-2" />
              )}
              {appointment ? 'Update Appointment' : 'Schedule Appointment'}
            </button>
          </div>
        </form>
      </div>
    </div>
  )
}

export default AppointmentForm
