import React, { useState, useEffect } from 'react'
import { supabase } from '../lib/supabase'
import { Search, Plus, Edit, Eye, Trash2, FlaskConical, FileText, User, Calendar } from 'lucide-react'
import LabResultForm from './forms/LabResultForm'
import toast from 'react-hot-toast'

const LabResultsPage = () => {
  const [labResults, setLabResults] = useState([])
  const [loading, setLoading] = useState(true)
  const [searchTerm, setSearchTerm] = useState('')
  const [statusFilter, setStatusFilter] = useState('all')
  const [selectedLabResult, setSelectedLabResult] = useState(null)
  const [showModal, setShowModal] = useState(false)

  useEffect(() => {
    fetchLabResults()
  }, [])

  const fetchLabResults = async () => {
    try {
      setLoading(true)
      const { data, error } = await supabase
        .from('lab_results')
        .select(`
          *,
          patients(first_name, last_name, email),
          doctors(first_name, last_name, specialization)
        `)
        .order('test_date', { ascending: false })

      if (error) throw error
      setLabResults(data || [])
    } catch (error) {
      console.error('Error fetching lab results:', error)
      toast.error('Failed to load lab results')
    } finally {
      setLoading(false)
    }
  }

  const filteredLabResults = labResults.filter(result => {
    const matchesSearch = 
      result.patients?.first_name?.toLowerCase().includes(searchTerm.toLowerCase()) ||
      result.patients?.last_name?.toLowerCase().includes(searchTerm.toLowerCase()) ||
      result.test_name?.toLowerCase().includes(searchTerm.toLowerCase()) ||
      result.doctors?.first_name?.toLowerCase().includes(searchTerm.toLowerCase()) ||
      result.doctors?.last_name?.toLowerCase().includes(searchTerm.toLowerCase())
    
    const matchesStatus = statusFilter === 'all' || result.status === statusFilter
    
    return matchesSearch && matchesStatus
  })

  const handleAddLabResult = () => {
    setSelectedLabResult(null)
    setShowModal(true)
  }

  const handleEditLabResult = (labResult) => {
    setSelectedLabResult(labResult)
    setShowModal(true)
  }

  const handleDeleteLabResult = async (labResultId) => {
    if (window.confirm('Are you sure you want to delete this lab result?')) {
      try {
        const { error } = await supabase
          .from('lab_results')
          .delete()
          .eq('id', labResultId)

        if (error) throw error
        toast.success('Lab result deleted successfully!')
        fetchLabResults()
      } catch (error) {
        console.error('Error deleting lab result:', error)
        toast.error('Failed to delete lab result')
      }
    }
  }

  const handleFormSuccess = (labResult) => {
    fetchLabResults()
    setShowModal(false)
    setSelectedLabResult(null)
  }

  const handleCloseModal = () => {
    setShowModal(false)
    setSelectedLabResult(null)
  }

  const getStatusColor = (status) => {
    switch (status) {
      case 'normal': return 'bg-green-100 text-green-800'
      case 'abnormal': return 'bg-yellow-100 text-yellow-800'
      case 'critical': return 'bg-red-100 text-red-800'
      default: return 'bg-gray-100 text-gray-800'
    }
  }

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric'
    })
  }

  if (loading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-primary-600"></div>
      </div>
    )
  }

  return (
    <div className="p-6">
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-3xl font-bold text-gray-900">Lab Results</h1>
        <button 
          onClick={handleAddLabResult}
          className="bg-primary-600 text-white px-4 py-2 rounded-md hover:bg-primary-700 flex items-center"
        >
          <Plus className="w-4 h-4 mr-2" />
          Add Lab Result
        </button>
      </div>

      {/* Filters */}
      <div className="mb-6 space-y-4">
        <div className="flex flex-col md:flex-row gap-4">
          <div className="flex-1">
            <div className="relative">
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 w-5 h-5" />
              <input
                type="text"
                placeholder="Search lab results..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="w-full pl-10 pr-4 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-primary-500"
              />
            </div>
          </div>
          <div className="md:w-48">
            <select
              value={statusFilter}
              onChange={(e) => setStatusFilter(e.target.value)}
              className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-primary-500"
            >
              <option value="all">All Results</option>
              <option value="normal">Normal</option>
              <option value="abnormal">Abnormal</option>
              <option value="critical">Critical</option>
            </select>
          </div>
        </div>
      </div>

      {/* Lab Results Table */}
      <div className="bg-white shadow rounded-lg overflow-hidden">
        <table className="min-w-full divide-y divide-gray-200">
          <thead className="bg-gray-50">
            <tr>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                Patient & Doctor
              </th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                Test Information
              </th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                Results
              </th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                Status
              </th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                Actions
              </th>
            </tr>
          </thead>
          <tbody className="bg-white divide-y divide-gray-200">
            {filteredLabResults.map((result) => (
              <tr key={result.id} className="hover:bg-gray-50">
                <td className="px-6 py-4 whitespace-nowrap">
                  <div className="flex items-center">
                    <div className="h-10 w-10 rounded-full bg-gray-300 flex items-center justify-center">
                      <User className="w-5 h-5 text-gray-600" />
                    </div>
                    <div className="ml-4">
                      <div className="text-sm font-medium text-gray-900">
                        {result.patients?.first_name} {result.patients?.last_name}
                      </div>
                      <div className="text-sm text-gray-500">
                        Dr. {result.doctors?.first_name} {result.doctors?.last_name}
                      </div>
                    </div>
                  </div>
                </td>
                <td className="px-6 py-4 whitespace-nowrap">
                  <div className="flex items-center">
                    <FlaskConical className="w-4 h-4 text-gray-400 mr-2" />
                    <div>
                      <div className="text-sm font-medium text-gray-900">
                        {result.test_name}
                      </div>
                      <div className="text-sm text-gray-500">
                        {formatDate(result.test_date)}
                      </div>
                    </div>
                  </div>
                </td>
                <td className="px-6 py-4 whitespace-nowrap">
                  <div>
                    <div className="text-sm font-medium text-gray-900">
                      {result.result_value} {result.unit}
                    </div>
                    {result.normal_range && (
                      <div className="text-sm text-gray-500">
                        Normal: {result.normal_range}
                      </div>
                    )}
                  </div>
                </td>
                <td className="px-6 py-4 whitespace-nowrap">
                  <span className={`inline-flex px-2 py-1 text-xs font-semibold rounded-full ${getStatusColor(result.status)}`}>
                    {result.status.toUpperCase()}
                  </span>
                </td>
                <td className="px-6 py-4 whitespace-nowrap text-sm font-medium">
                  <div className="flex space-x-2">
                    <button
                      onClick={() => handleEditLabResult(result)}
                      className="text-primary-600 hover:text-primary-900"
                      title="Edit lab result"
                    >
                      <Edit className="w-4 h-4" />
                    </button>
                    <button className="text-gray-600 hover:text-gray-900" title="View details">
                      <Eye className="w-4 h-4" />
                    </button>
                    <button
                      onClick={() => handleDeleteLabResult(result.id)}
                      className="text-red-600 hover:text-red-900"
                      title="Delete lab result"
                    >
                      <Trash2 className="w-4 h-4" />
                    </button>
                  </div>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {/* Lab Results Count */}
      <div className="mt-4 text-sm text-gray-600">
        Showing {filteredLabResults.length} of {labResults.length} lab results
      </div>

      {/* Lab Result Form Modal */}
      {showModal && (
        <LabResultForm
          labResult={selectedLabResult}
          onClose={handleCloseModal}
          onSuccess={handleFormSuccess}
        />
      )}
    </div>
  )
}

export default LabResultsPage
